package com.example.hw8

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.example.hw8.databinding.FragmentFirstBinding

class FirstFragment : Fragment() {
    private lateinit var binding: FragmentFirstBinding

    var list : ArrayList<CardModel> = arrayListOf(
        CardModel("data:image/jpeg;base64,",
            "Alive", "Rick Sanchez"),
        CardModel("https://static.wikia.nocookie.net/rickandmorty/images/e/ee/Morty501.png/revision/latest/thumbnail/width/360/height/360?cb=20210827150137",
            "Alive", "Morty Smith"),
        CardModel("https://static.wikia.nocookie.net/rickandmorty/images/b/bc/Albert_Einstein.png/revision/latest/smart/width/250/height/250?cb=20150730213810",
            "Dead", "Albert Einstein"),
        CardModel("data:image/jpeg;base64,",
            "Alive", "Jerry Smith")
    )
    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentFirstBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        val adapter  = CardAdapter(list, this::onClick)
        binding.rv.adapter = adapter
    }

    private fun onClick(card: CardModel) {
        findNavController().navigate(FirstFragmentDirections.actionFirstFragmentToSecondFragment(card))
    }
}